// Morris Donut
Morris.Donut({
	element: 'donutColors',
	data: [
		{value: 70, label: '37'},
		{value: 30, label: ''},
	],
	backgroundColor: '#ffffff',
	labelColor: '#67A5EC',
	colors:['#67A5EC', '#e5e8f2'],
	resize: true,
	hideHover: "auto",
	gridLineColor: "#e4e6f2",
	formatter: function (x) { return x + "%"}
});
